import React, { Component } from 'react'
import NewsComponent from './NewsComponent'
 

export class News extends Component {
  articles = []
    constructor()
    {
        super();
        this.state={
            articles : this.articles,
            loading: false,
            page:1
            
        }
    }
    async componentDidMount()
    {
       let url="https://newsapi.org/v2/top-headlines?&apiKey=0516ff4fe6e44803b32e7e321ccfd96f&page=1&pageSize=9";
       let data =await fetch(url);
       let parsedData= await data.json()
       this.setState({articles:parsedData.articles, totalResults:parsedData.totalResults })

    }
    previousClick= async()=>{
       let url=`https://newsapi.org/v2/top-headlines?&apiKey=0516ff4fe6e44803b32e7e321ccfd96f&page=${this.state.page+1}&pageSize=9`;
       let data =await fetch(url);
       let parsedData= await data.json()
       
        this.setState(
          {
            page:this.state.page-1,
            articles:parsedData.articles
          }
        )

    }
    nextClick= async()=>{
      if( this.state.page+1 > Math.ceil(this.state.totalResults/9))
      {

      }
      else{
       let url=`https://newsapi.org/v2/top-headlines?&apiKey=0516ff4fe6e44803b32e7e321ccfd96f&page=${this.state.page+1}&pageSize=9`;
       let data =await fetch(url);
       let parsedData= await data.json()
       
        this.setState(
          {
            page:this.state.page+1,
            articles:parsedData.articles
          }
        
        )
        }
    }
  render() {
    return (
      <div className='container my-2'>
        <h2>Top Headlines</h2>
        
        <div className="row">
        {this.state.articles.map((element)=>{
            
          return <div className="col-md-4" key={element.url} >
           <NewsComponent  title={element.title?element.title:""} description={element.description?element.description:""} imageUrl={element.urlToImage} newsUrl={element.url}/>
                </div> 
        })}
            
        </div>
            <div className="d-grid gap-2 d-md-flex justify-content-md-end">
            <button disabled={this.state.page<=1} className="btn btn-sm btn-dark me-md-2" onClick={this.previousClick} type="button">&larr; previous</button>
            <button className="btn btn-sm btn-dark" onClick={this.nextClick} type="button">next &rarr;</button>
       </div>
      
      </div>
    )
  }
}

export default News
