import React, { useState } from "react";

const Footer=()=>  {
       const[submit,setSubmit]=useState(false);
     const [form,setForm]=useState({});
     const[data,setData]=useState({});
        const handleForm=(e)=>{
          
          setForm({
            ...form,
            [e.target.name]:e.target.value
          })
        }
       const handleSubmit= async (e)=>{
          e.preventDefault();
        const response = await fetch('http://localhost:8080/contact',{
            method:'POST',
            body:JSON.stringify(form),
            headers:{
              'content-Type':'application/json'
            }
          })
          const data = await response.json();

          setData(data);
          setSubmit(true);
       }
    return (
      <div >
        <div className="card text-center bg-dark text-muted">
          <div className="card-header ">CONTACT US</div>
         
          <div className="card-body">
            <h5 className="card-title">News Castify</h5>
            <p className="card-text">Provides you most recently news here.</p>
          </div>
          <div className="card-footer text-muted">
            Developed By:
            <a
              href={"https://github.com/Dev4821"}
              className="link-light text-decoration-none"
              target="_blank"
              rel="noreferrer"
            >
               Divyansh , Himanshu and Ana
            </a>
          </div>
        </div>
      </div>
    );
  
}

export default Footer;
